import React from 'react'
import styled from 'styled-components'
import Picker from "emoji-picker-react"
import { IoMdSend} from "react-icons/io"
import {BsEmojiSmileFill} from "react-icons/bs"
function Chatinput() {
  return (
      <>
      <Container>
          <div className='button-contaier'>
              <div className='emojy'>
                  <BsEmojiSmileFill/>
              </div>
          </div>
          <form>
              <input type='text ' placeholder="type your message here"/>
              <button className='submit'> 
               <IoMdSend/>
              </button>
              </form>
      </Container>

      </>
  )
}

const Container = styled.div`
    display: grid;
    grid-template-columns: 5% 95%;
    align-items: center; 
    background-color: aliceblue;
    padding: 0 2rem;
    padding-bottom:0.3rem;
    .button-container{
        display: flex;

        .emoji{
            position:relative;
            svg{
                font-size: 1.5rem;
                color:#ffff00c8;
                cursor: pointer;
            }
        }
    }
    .input-container{
    width: 100%; 
    border-radius:2rem;
        display: flex;
        align-content: center;
        gap:2rem;
        background-color: #31312dc7;
        input{
            width: 90%;
            height: 60%;
            background-color: transparent;
            color: white;
            border: none;
            padding: 1rem;
            font-size:1.2rem;
            &:focus{
                outline: none;
            }
        }
        button{
            padding: 0.3rem 2rem;
            border-radius: 2rem;
            display: flex;
            justify-content: center;
            background-color:#5b14e7;
            border: none;
            svg{
                font-size:2rem ;
                color: white;
            }

        }
    }
`
export default Chatinput
